import rollupBase from './rollup.base';
import uglify from 'rollup-plugin-uglify';
const pkg = require('../package');

rollupBase.output.splice(1, rollupBase.output.length - 1);
rollupBase.output[0].file = `dist/${pkg.name}.min.js`;

rollupBase.plugins.push(uglify());

export default Object.assign({}, rollupBase);
